using Okta.Sdk;\nusing Okta.Sdk.Configuration;\n\n// Initialize Okta client\nvar oktaClient = new OktaClient(new OktaClientConfiguration\n{\n    OrgUrl = "https://your-okta-domain.com",\n    Token = "your-api-token"\n});\n\n// Register a new user\nvar user = new User\n{\n    Profile = new UserProfile\n    {\n        Email = "user@example.com",\n        FirstName = "John",\n        LastName = "Doe"\n    },\n    Credentials = new UserCredentials\n    {\n        Password = new PasswordCredential\n        {\n            Value = "strongpassword"\n        }\n    }\n};\noktaClient.Users.CreateUserAsync(user).Wait();\n\n// Authenticate a user\nvar authResponse = oktaClient.Authn.AuthenticateAsync(new AuthenticateRequest\n{\n    Username = "user@example.com",\n    Password = "strongpassword"\n}).Result;