public bool CopyWorksheet(Worksheet ws, String sourceSheet)\n{\n    try\n    {\n        try\n        {\n            Worksheet sheet = GetWorksheet(sourceSheet + " (2)");\n            Marshal.ReleaseComObject(sheet);\n            sheet = null;\n            return false;\n        }\n        catch (Exception)\n        {\n            ws.Copy(Missing.Value, ws);\n            Thread.Sleep(100); // add a delay\n        }\n        return true;\n    }\n    catch (Exception)\n    {\n        return false;\n    }\n    finally\n    {\n        ws.Activate();\n        Marshal.ReleaseComObject(ws); // release the worksheet object\n        ws = null;\n    }\n}