public class SqlStatementFactory\n{\n    public ResourceManager GetResourceManager(string databaseType)\n    {\n        switch (databaseType)\n        {\n            case "Access":\n                return new ResourceManager(typeof(AccessSqlStatements));\n            case "SqlServer":\n                return new ResourceManager(typeof(SqlServerSqlStatements));\n            default:\n                throw new NotSupportedException();\n        }\n    }\n}