using Microsoft.ReportingServices;\n\n// Create a new parameter\nvar param = new Parameter();\nparam.Name = "Location";\nparam.DefaultValue = "Original Value";\n\n// Create a new snapshot schedule\nvar schedule = new Schedule();\nschedule.Report = "My Report";\nschedule.Format = "PDF";\nschedule.Extension = "SSRS";\nschedule.Parameters.Add(param);\nschedule.ScheduleDefinition = new ScheduleDefinition();\nschedule.ScheduleDefinition.Frequency = ScheduleFrequency.Weekly;\n\n// Set the new parameter value for the snapshot schedule\nschedule.Parameters["@Location"].DefaultValue = "New Value";