using SudokuSolver;\n\n// Create a Sudoku grid\nint[,] grid = {\n    {5, 3, 0, 0, 7, 0, 0, 0, 0},\n    {6, 0, 0, 1, 9, 5, 0, 0, 0},\n    {0, 9, 8, 0, 0, 0, 0, 6, 0},\n    {8, 0, 0, 0, 6, 0, 0, 0, 3},\n    {4, 0, 0, 8, 0, 3, 0, 0, 1},\n    {7, 0, 0, 0, 2, 0, 0, 0, 6},\n    {0, 6, 0, 0, 0, 0, 2, 8, 0},\n    {0, 0, 0, 4, 1, 9, 0, 0, 5},\n    {0, 0, 0, 0, 8, 0, 0, 7, 9}\n};\n\n// Solve the Sudoku puzzle\nSudokuSolver solver = new SudokuSolver(grid);\nbool solved = solver.Solve();\n\n// Print the solved grid\nif (solved) {\n    Console.WriteLine("Solved Sudoku grid:");\n    for (int i = 0; i < 9; i++) {\n        for (int j = 0; j < 9; j++) {\n            Console.Write(grid[i, j] + " ");\n        }\n        Console.WriteLine();\n    }\n} else {\n    Console.WriteLine("No solution found.");\n}