using System.IO;\nusing System.Security.AccessControl;\nusing System.Security.Principal;\n\n// Get the current user\nvar currentUser = WindowsIdentity.GetCurrent();\n\n// Create a new directory with permissions for only the current user\nvar directoryPath = @"C:\NewDirectory";\nvar directorySecurity = new DirectorySecurity();\ndirectorySecurity.AddAccessRule(new FileSystemAccessRule(currentUser.Name, FileSystemRights.FullControl, AccessControlType.Allow));\n\nDirectory.CreateDirectory(directoryPath, directorySecurity);