using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("kernel32.dll")]\n    static extern uint SetLocaleInfo(uint Locale, uint LCType, string lpLCData);\n\n    const uint LOCALE_USER_DEFAULT = 0x0400;\n    const uint LOCALE_SSHORTDATE = 0x1F;\n\n    static void Main()\n    {\n        string userAccount = "YourServiceAccountName";\n        string shortDateFormat = "dd/MM/yyyy"; // Set the desired short date format\n\n        // Load user profile\n        IntPtr userHandle = IntPtr.Zero;\n        bool loaded = NativeMethods.LoadUserProfile(userAccount, out userHandle);\n        if (!loaded)\n        {\n            Console.WriteLine("Failed to load user profile");\n            return;\n        }\n\n        try\n        {\n            // Set the short date format for the user\n            uint result = SetLocaleInfo(LOCALE_USER_DEFAULT, LOCALE_SSHORTDATE, shortDateFormat);\n            if (result == 0)\n            {\n                Console.WriteLine("Failed to set short date format");\n            }\n        }\n        finally\n        {\n            // Unload user profile\n            NativeMethods.UnloadUserProfile(userHandle);\n        }\n    }\n}\n\ninternal static class NativeMethods\n{\n    [DllImport("userenv.dll", CharSet = CharSet.Unicode, SetLastError = true)]\n    internal static extern bool LoadUserProfile(string lpUserName, out IntPtr phToken);\n\n    [DllImport("userenv.dll", SetLastError = true)]\n    internal static extern bool UnloadUserProfile(IntPtr hProfile);\n}