// On domain A (Master)\npublic class CookieController : Controller\n{\n    [HttpPost]\n    public ActionResult ShareCookie(string cookieValue)\n    {\n        // Validate and sanitize cookieValue\n        // Call web service on domain B to set the cookie\n        using (var client = new WebClient())\n        {\n            client.UploadString("https://domainB.com/cookie-service", cookieValue);\n        }\n        return Ok();\n    }\n}\n\n// On domain B (Slave)\npublic class CookieServiceController : Controller\n{\n    [HttpPost]\n    public ActionResult SetCookie(string cookieValue)\n    {\n        // Validate and sanitize cookieValue\n        Response.Cookies.Add(new HttpCookie("sharedCookie", cookieValue));\n        return Ok();\n    }\n}