using Microsoft.Office.Interop.Excel;\n\n// Create a new Excel application instance\nApplication excelApp = new Application();\n\n// Create a new workbook\nWorkbook workbook = excelApp.Workbooks.Add();\n\n// Add a new worksheet\nWorksheet worksheet = workbook.Worksheets.Add();\n\n// Load the image\nImage image = Image.FromFile("path_to_your_image.jpg");\n\n// Convert the image to a byte array\nbyte[] imageBytes = ImageToByteArray(image);\n\n// Add the image to the worksheet\nworksheet.Shapes.AddPicture(imageBytes, Microsoft.Office.Interop.Excel.XlPictureFormat.xlBMP, 0, 0, 100, 100);\n\n// Save the workbook to a file\nworkbook.SaveAs("path_to_your_excel_file.xls");\n\n// Release resources\nworkbook.Close();\nexcelApp.Quit();\n\n// Helper function to convert image to byte array\nprivate byte[] ImageToByteArray(System.Drawing.Image image)\n{\n    using (MemoryStream ms = new MemoryStream())\n    {\n        image.Save(ms, System.Drawing.Imaging.ImageFormat.Jpeg);\n        return ms.ToArray();\n    }\n}