public class NullableDateBindingHandler : BindingHandler\n{\n    public override object GetValue(object control, string name)\n    {\n        TextBox textBox = (TextBox)control;\n        string value = textBox.Text.Trim();\n        DateTime date;\n        if (string.IsNullOrEmpty(value) || !DateTime.TryParseExact(value, "MM/dd/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out date))\n        {\n            return DBNull.Value;\n        }\n        return date;\n    }\n}