[DllImport("ril.dll")]\nprivate static extern int RIL_GetSignalStrength(ref RIL_SIGNAL_STRENGTH signalStrength);\n\n[DllImport("ril.dll")]\nprivate static extern int RIL_GetCurrentChannelNumber(ref int channelNumber);\n\n// Define the RIL_SIGNAL_STRENGTH structure\n[StructLayout(LayoutKind.Sequential)]\npublic struct RIL_SIGNAL_STRENGTH\n{\n    public int dwParam1;\n    public int dwParam2;\n}\n\npublic class NetworkInfo\n{\n    public int GetSignalStrength()\n    {\n        RIL_SIGNAL_STRENGTH signalStrength = new RIL_SIGNAL_STRENGTH();\n        int result = RIL_GetSignalStrength(ref signalStrength);\n        if (result == 0)\n        {\n            return signalStrength.dwParam1;\n        }\n        else\n        {\n            return -1; // Error\n        }\n    }\n\n    public int GetCurrentChannelNumber()\n    {\n        int channelNumber = 0;\n        int result = RIL_GetCurrentChannelNumber(ref channelNumber);\n        if (result == 0)\n        {\n            return channelNumber;\n        }\n        else\n        {\n            return -1; // Error\n        }\n    }\n}