using System;\nusing System.Threading;\nusing System.Diagnostics;\n\npublic class MyForm : Form\n{\n    private static Mutex _mutex;\n    private static string _processId;\n\n    public MyForm()\n    {\n        _processId = Process.GetCurrentProcess().Id.ToString();\n        _mutex = new Mutex(false, "MyForm_" + _processId);\n    }\n\n    public void ShowForm()\n    {\n        if (_mutex.WaitOne(TimeSpan.Zero, false))\n        {\n            try\n            {\n                Show();\n            }\n            finally\n            {\n                _mutex.ReleaseMutex();\n            }\n        }\n        else\n        {\n            // An instance of MyForm is already running for this process\n        }\n    }\n}