// First, delete the relationships (ComponentContext table)\nforeach (var component in CPE.Component)\n{\n    foreach (var context in component.Context)\n    {\n        component.Context.Remove(context);\n    }\n}\n\n// Save the changes to delete the relationships\nCPE.SaveChanges();\n\n// Now, delete the entities (Component and Context tables)\nforeach (var component in CPE.Component)\n{\n    CPE.DeleteObject(component);\n}\n\nforeach (var context in CPE.Context)\n{\n    CPE.DeleteObject(context);\n}\n\n// Save the changes to delete the entities\nCPE.SaveChanges();