using Microsoft.TeamFoundation.Client;\nusing Microsoft.TeamFoundation.VersionControl.Client;\n\nTfsTeamProjectCollection collection = new TfsTeamProjectCollection(new Uri("http://your-tfs-server:8080/tfs/your-collection"));\nVersionControlServer vcs = collection.GetService<VersionControlServer>();\n\nLabel label = vcs.GetLabel("your-label-name");\nint changesetId = label.ChangesetId;\n\nConsole.WriteLine($"Changeset ID: {changesetId}");