private Dictionary<PricingStrategy, PricingModel> strategies = new Dictionary<PricingStrategy, PricingModel>\n{\n    { PricingStrategy.STRATEGY_X, new PricingModel(PricingModelStrategyX) },\n    { PricingStrategy.STRATEGY_Y, new PricingModel(PricingModelStrategyY) }\n};\n\npublic void SwitchStrategy(PricingStrategy strategy)\n{\n    if (strategies.TryGetValue(strategy, out PricingModel model))\n    {\n        myPricingModel = model;\n    }\n}