using System;\nusing System.Runtime.InteropServices;\nusing SHDocVw;\n\nclass IEController\n{\n    private InternetExplorer _ie;\n\n    public IEController()\n    {\n        _ie = new InternetExplorer();\n        _ie.Visible = true;\n    }\n\n    public void Navigate(string url)\n    {\n        _ie.Navigate(url);\n    }\n\n    public void ClickButton(string buttonId)\n    {\n        HTMLDocument doc = _ie.Document;\n        HTMLInputElement button = doc.getElementById(buttonId) as HTMLInputElement;\n        if (button != null)\n        {\n            button.click();\n        }\n    }\n\n    public string GetControlValue(string controlId)\n    {\n        HTMLDocument doc = _ie.Document;\n        HTMLInputElement control = doc.getElementById(controlId) as HTMLInputElement;\n        if (control != null)\n        {\n            return control.value;\n        }\n        return string.Empty;\n    }\n\n    public void SetControlValue(string controlId, string value)\n    {\n        HTMLDocument doc = _ie.Document;\n        HTMLInputElement control = doc.getElementById(controlId) as HTMLInputElement;\n        if (control != null)\n        {\n            control.value = value;\n        }\n    }\n}