using System;\nusing Microsoft.AspNetCore.Mvc;\n\nnamespace Benchmark.Controllers\n{\n    [ApiController]\n    [Route("api/[controller]")]\n    public class BenchmarkController : ControllerBase\n    {\n        [HttpGet]\n        public IActionResult Get()\n        {\n            int result = 0;\n            for (int i = 0; i < 10000000; i++)\n            {\n                result += i;\n            }\n            return Ok(result);\n        }\n    }\n}