using Castle.ActiveRecord;\nusing Castle.ActiveRecord.Generator;\n\n// Create a new instance of the generator\nvar generator = new ActiveRecordGenerator();\n\n// Specify the database connection and schema\ngenerator.Database = new SqlServerDatabase("your_database_connection_string");\ngenerator.Schema = "your_schema_name";\n\n// Generate the classes\ngenerator.Generate();\n\n// Save the generated classes to a file\ngenerator.SaveGeneratedClasses("path/to/output/folder");