using Microsoft.Win32.FileSystem;\n\n// Create a FileOperationWatcher instance\nvar watcher = new FileOperationWatcher(@"E:\", FileOperationFlags.Delete);\n\n// Set up an event handler for the deletion event\nwatcher.OperationCompleted += (sender, args) => {\n    if (args.Operation == FileOperation.Delete) {\n        // Get the file being deleted\n        var file = args.FileName;\n        // Copy the file to your "recycle bin"\n        // ...\n    }\n};\n\n// Start the watcher\nwatcher.Start();