protected void Page_Load(object sender, EventArgs e)\n{\n    string goalId = Request.QueryString["id"];\n    string externalUrl = "http://www.externallink.co.uk/GoalAccessed.aspx?id=" + goalId;\n\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(externalUrl);\n    HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n    Response.Write(response.GetResponseStream());\n}