[Serializable]\npublic class ShoppingCart\n{\n    public List<CartItem> Items { get; set; }\n}\n\npublic class CartItem\n{\n    public string ProductName { get; set; }\n    public int Quantity { get; set; }\n}\n\n// In your profile class\npublic class CustomProfile : ProfileBase\n{\n    [SettingsAllowAnonymous(false)]\n    public ShoppingCart ShoppingCart\n    {\n        get\n        {\n            string cartData = base.GetPropertyValue("ShoppingCart") as string;\n            if (!string.IsNullOrEmpty(cartData))\n            {\n                byte[] bytes = Convert.FromBase64String(cartData);\n                using (MemoryStream stream = new MemoryStream(bytes))\n                {\n                    BinaryFormatter formatter = new BinaryFormatter();\n                    return formatter.Deserialize(stream) as ShoppingCart;\n                }\n            }\n            return new ShoppingCart();\n        }\n        set\n        {\n            using (MemoryStream stream = new MemoryStream())\n            {\n                BinaryFormatter formatter = new BinaryFormatter();\n                formatter.Serialize(stream, value);\n                string cartData = Convert.ToBase64String(stream.ToArray());\n                base.SetPropertyValue("ShoppingCart", cartData);\n            }\n        }\n    }\n}