using System.Security.Cryptography;\n\npublic void StoreCredentials(string username, string password)\n{\n    byte[] encryptedBytes = ProtectedData.Protect(Encoding.UTF8.GetBytes(password), null, DataProtectionScope.LocalMachine);\n    // Store the encrypted bytes securely\n}\n\npublic string GetCredentials()\n{\n    byte[] encryptedBytes = // Retrieve the stored encrypted bytes\n    byte[] decryptedBytes = ProtectedData.Unprotect(encryptedBytes, null, DataProtectionScope.LocalMachine);\n    return Encoding.UTF8.GetString(decryptedBytes);\n}