using Oracle.ManagedDataAccess.Client;\n\n// Create a new Oracle connection\nOracleConnection connection = new OracleConnection("User Id=<username>;Password=<password>;Data Source=<datasource>");\n\ntry\n{\n    // Open the connection\n    connection.Open();\n\n    // Create a new Oracle command\n    OracleCommand command = connection.CreateCommand();\n\n    // Set the command text to the SQL script\n    command.CommandText = @"\n        CREATE TABLE TestTable (\n            Column1 VARCHAR2(20),\n            Column2 NUMBER\n        );\n\n        INSERT INTO TestTable (Column1, Column2)\n        VALUES ('Test', 123);\n\n        CREATE INDEX TestIndex ON TestTable (Column1);\n    ";\n\n    // Set the command type to text\n    command.CommandType = CommandType.Text;\n\n    // Execute the command\n    command.ExecuteNonQuery();\n}\nfinally\n{\n    // Close the connection\n    connection.Close();\n}