using System;\nusing System.Diagnostics;\nusing System.Windows.Forms;\n\npublic class VpnStatusControl : Label\n{\n    private string _vpnProcessName = "YourVpnProcessName"; // Replace with the actual VPN process name\n\n    public VpnStatusControl()\n    {\n        this.Text = "VPN Status: Unknown";\n        this.UpdateStatus();\n    }\n\n    private void UpdateStatus()\n    {\n        if (IsVpnProcessRunning())\n        {\n            this.Text = "VPN Status: Connected";\n            this.ForeColor = Color.Green;\n        }\n        else\n        {\n            this.Text = "VPN Status: Disconnected";\n            this.ForeColor = Color.Red;\n        }\n    }\n\n    private bool IsVpnProcessRunning()\n    {\n        return Process.GetProcessesByName(_vpnProcessName).Length > 0;\n    }\n\n    private Timer _timer;\n\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n\n        _timer = new Timer();\n        _timer.Interval = 1000; // Check every 1 second\n        _timer.Tick += (sender, args) => UpdateStatus();\n        _timer.Start();\n    }\n\n    protected override void Dispose(bool disposing)\n    {\n        base.Dispose(disposing);\n\n        if (disposing)\n        {\n            _timer?.Dispose();\n        }\n    }\n}