using Oracle.ManagedDataAccess.Client;\nusing Oracle.SqlPlus;\n\n// Create a new Oracle connection\nOracleConnection connection = new OracleConnection("User Id=<username>;Password=<password>;Data Source=<datasource>");\n\ntry\n{\n    // Open the connection\n    connection.Open();\n\n    // Create a new SqlPlusEngine instance\n    SqlPlusEngine engine = new SqlPlusEngine(connection);\n\n    // Execute the SQL script\n    engine.ExecuteFile("path/to/script.sql");\n}\nfinally\n{\n    // Close the connection\n    connection.Close();\n}