using System.Data.Entity;\nusing Oracle.ManagedDataAccess.Client;\n\n// Create a DbContext class\npublic class MyDbContext : DbContext\n{\n    public MyDbContext() : base("OracleDbContext")\n    {\n    }\n\n    // Define your entities (e.g. tables)\n    public DbSet<MyTable> MyTables { get; set; }\n}\n\n// Define your entity (e.g. table)\npublic class MyTable\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// Create a connection string\nstring connectionString = "Data Source=myoracledb;User Id=myuser;Password=mypassword;";\n\n// Create a DbContext instance\nMyDbContext dbContext = new MyDbContext();\n\n// Use the DbContext to perform CRUD operations\ndbContext.MyTables.Add(new MyTable { Name = "John Doe" });\ndbContext.SaveChanges();