using System;\nusing System.Data.OleDb;\n\n// Assuming you have an OleDbConnection to your Access database\nOleDbConnection conn = new OleDbConnection("your_connection_string");\n\n// Create a command to update the field\nOleDbCommand cmd = new OleDbCommand("UPDATE YourTableName SET YourFieldName = ?", conn);\n\n// Use the DateTime.ParseExact method to parse the text dates\ncmd.Parameters.AddWithValue("@date", DateTime.ParseExact("3/12/2009 11:32:40 PM", "MM/dd/yyyy hh:mm:ss tt", CultureInfo.InvariantCulture));\n\n// Execute the command\ncmd.ExecuteNonQuery();