using System.Data.Linq;\nusing System.Xml.XPath;\n\n// assume 'context' is your DataContext and 'Author' is your table\nvar query = context.Author\n    .Where(a => a.XmlField.XPathSelectElement("Root/CreatedAt").Value == DateTime.Now.ToString());\n\n// or using XPathSelectElements if you need to select multiple elements\nvar query = context.Author\n    .Where(a => a.XmlField.XPathSelectElements("Root/CreatedAt").Any(e => e.Value == DateTime.Now.ToString()));