using System.Security.Cryptography.X509Certificates;\n\npublic class CertificateGenerator\n{\n    public static void GenerateCertificate(string subjectName, string certPath)\n    {\n        using (var rsa = RSA.Create(2048))\n        {\n            var certRequest = new CertificateRequest(subjectName, rsa, HashAlgorithmName.SHA256, RSASignaturePadding.Pkcs1);\n            var cert = certRequest.CreateSelfSigned(DateTime.UtcNow, DateTime.UtcNow.AddYears(10));\n\n            using (var stream = new FileStream(certPath, FileMode.Create))\n            {\n                cert.Export(X509ContentType.Pfx, "password").CopyTo(stream);\n            }\n        }\n    }\n}