using System.Runtime.InteropServices;\n\nclass ComComponentRegistrar\n{\n    [DllImport("ole32.dll")]\n    static extern int CoRegisterClassObject(ref Guid rclsid, [MarshalAs(UnmanagedType.IUnknown)] object pUnk, int dwClsContext, int flags, out int lpdwRegister);\n\n    public static void RegisterComComponent(string dllPath)\n    {\n        Guid clsid = new Guid("YOUR_COM_COMPONENT_CLSID");\n        int registerResult = 0;\n\n        CoRegisterClassObject(ref clsid, null, 1, 0, out registerResult);\n    }\n}