using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("ole32.dll")]\n    static extern int CoCreateInstance(ref Guid clsid, [MarshalAs(UnmanagedType.IUnknown)] object punkOuter, \n        int context, ref Guid iid, out IntPtr punk);\n\n    static void Main(string[] args)\n    {\n        Guid mediaPlayerClassId = new Guid("47AC3C2F-7033-4D47-AE81-9C94E566C4CC");\n        Guid iid = new Guid("00000000-0000-0000-C000-000000000046"); // IUnknown\n\n        IntPtr punk;\n        int hr = CoCreateInstance(ref mediaPlayerClassId, null, 1, ref iid, out punk);\n\n        if (hr == 0)\n        {\n            Console.WriteLine("COM component created successfully");\n        }\n        else\n        {\n            Console.WriteLine("Error creating COM component: " + hr);\n        }\n    }\n}