using log4net;\n\npublic class MyClass\n{\n    private readonly ILog _logger;\n\n    public MyClass()\n    {\n        _logger = LogManager.GetLogger(typeof(MyClass));\n    }\n\n    public void DoSomething()\n    {\n        try\n        {\n            // Do something\n        }\n        catch (Exception ex)\n        {\n            _logger.Error("Something went wrong", ex);\n        }\n    }\n}