using System.Data.SqlServerCe;\n\n// Create a connection to your SQL CE database\nSqlCeConnection conn = new SqlCeConnection("Data Source=your_database.sdf");\n\n// Create a command to retrieve the query execution plan\nSqlCeCommand cmd = conn.CreateCommand();\ncmd.CommandText = "SET SHOWPLAN ON; SELECT * FROM your_table";\n\n// Execute the command and read the results\nconn.Open();\nSqlCeDataReader reader = cmd.ExecuteReader();\n\n// Read the query execution plan\nwhile (reader.Read())\n{\n    Console.WriteLine(reader[0].ToString());\n}\n\n// Clean up\nreader.Close();\nconn.Close();