// .NET example\nusing NUnit.Framework;\nusing Moq;\n\n[TestFixture]\npublic class MyTest {\n    [Mock]\n    private MyDependency _dependency;\n\n    [Test]\n    public void TestMyMethod() {\n        // Arrange\n        _dependency.Setup(d => d.DoSomething()).Returns("mocked result");\n\n        // Act\n        var myClass = new MyClass(_dependency.Object);\n        var result = myClass.MyMethod();\n\n        // Assert\n        Assert.AreEqual("mocked result", result);\n    }\n}