string buildingFile = Server.MapPath("buildings.txt");\n\nusing (StreamReader fileStreamReader = File.OpenText(buildingFile))\n{\n    string line;\n    while ((line = fileStreamReader.ReadLine()) != null)\n    {\n        string[] columns = line.Split(',');\n\n        if (columns.Length >= 3)\n        {\n            string building = columns[0].Trim();\n            string person = columns[1].Trim();\n            string duty = columns[2].Trim();\n\n            switch (building)\n            {\n                case "bldgA":\n                    // Perform actions for bldgA\n                    Console.WriteLine($"You are in {building} and your name is {person} and your duty is {duty}");\n                    break;\n                case "bldgB":\n                    // Perform actions for bldgB\n                    Console.WriteLine($"You are in {building} and your name is {person} and your duty is {duty}");\n                    break;\n                default:\n                    // Handle unknown buildings\n                    Console.WriteLine($"Unknown building: {building}");\n                    break;\n            }\n        }\n        else\n        {\n            // Handle lines with insufficient columns\n            Console.WriteLine($"Invalid line format: {line}");\n        }\n    }\n}