var list1 = new[]\n{\n    new { Id = 1, Name = "Alice" },\n    new { Id = 2, Name = "Bob" },\n    new { Id = 3, Name = "Carol" },\n    new { Id = 4, Name = "Dave" }\n};\n\nvar list2 = new[]\n{\n    new { Id = 1, Value = 15 },\n    new { Id = 1, Value = 19 },\n    new { Id = 2, Value = 5 },\n    new { Id = 2, Value = 7 },\n    new { Id = 2, Value = 20 },\n    new { Id = 4, Value = 16 }\n};\n\nvar missingIds = list1.Select(x => x.Id).Except(list2.Select(x => x.Id));\n\nvar defaultValues = missingIds.Select(x => new { Id = x, Value = 0 });\n\nlist2 = list2.Concat(defaultValues);