using System.Data;\nusing System.Data.SqlClient;\n\npublic partial class StoredProcedures\n{\n    [SqlProcedure]\n    public static void BulkCopyData()\n    {\n        // Create a new regular connection\n        using (SqlConnection connection = new SqlConnection("Data Source=(local);Initial Catalog=YourDatabase;Integrated Security=True"))\n        {\n            connection.Open();\n\n            // Create a DataTable\n            DataTable table = new DataTable();\n            // populate your DataTable here...\n\n            // Create a SqlBulkCopy object\n            using (SqlBulkCopy bulkCopy = new SqlBulkCopy(connection))\n            {\n                bulkCopy.DestinationTableName = "YourDestinationTable";\n\n                // Map the columns\n                foreach (DataColumn column in table.Columns)\n                {\n                    bulkCopy.ColumnMappings.Add(column.ColumnName, column.ColumnName);\n                }\n\n                // Write to the database\n                bulkCopy.WriteToServer(table);\n            }\n        }\n    }\n}