protected void Button2_Click(object sender, EventArgs e)\n{\n    // ...\n\n    // Store the controls in ViewState\n    ViewState["Controls"] = pnl_showings.Controls;\n}\n\nprotected override void OnInit(EventArgs e)\n{\n    base.OnInit(e);\n\n    // Recreate the controls from ViewState\n    if (ViewState["Controls"] != null)\n    {\n        ControlCollection controls = (ControlCollection)ViewState["Controls"];\n        foreach (Control control in controls)\n        {\n            pnl_showings.Controls.Add(control);\n        }\n    }\n}