void UpdateNode(XmlDocument xml, string nodeMustExist, string nodeToBeUpdated, string newVal)\n{\n    // Load the XML document\n    XmlNode root = xml.DocumentElement;\n\n    // Find the node that must exist\n    XmlNode nodeMustExistNode = root.SelectSingleNode($"//{nodeMustExist}");\n\n    if (nodeMustExistNode != null)\n    {\n        // Find the node to be updated\n        XmlNode nodeToBeUpdatedNode = nodeMustExistNode.SelectSingleNode($"following-sibling::{nodeToBeUpdated}");\n\n        if (nodeToBeUpdatedNode != null)\n        {\n            // Update the node value\n            nodeToBeUpdatedNode.InnerText = newVal;\n        }\n        else\n        {\n            // Create a new node and insert it after the node that must exist\n            XmlNode newNode = xml.CreateElement(nodeToBeUpdated);\n            newNode.InnerText = newVal;\n            nodeMustExistNode.ParentNode.InsertAfter(newNode, nodeMustExistNode);\n        }\n    }\n}