using WinSCP;\n\n// Create a new WinSCP session\nSession session = new Session();\n\n// Connect to the SFTP server\nsession.Open(new SessionOptions\n{\n    Protocol = Protocol.SFTP,\n    HostName = "your_sftp_server",\n    UserName = "your_username",\n    Password = "your_password",\n});\n\n// Set the resume support\nsession.ResumeSupport = ResumeSupport.On;\n\n// Transfer the file\nTransferOperationResult result = session.PutFile("local_file", "remote_file");\n\n// Check the result\nif (result.IsSuccess)\n{\n    Console.WriteLine("File transferred successfully.");\n}\nelse\n{\n    Console.WriteLine("Error: " + result.Error.Message);\n}