public class ModuleManager : Form\n{\n    private PluginLoader _pluginLoader;\n\n    public ModuleManager()\n    {\n        _pluginLoader = new PluginLoader(@"C:\Plugins");\n        InitializeComponent();\n    }\n\n    private void InstallModule(string moduleName)\n    {\n        // Install module logic here\n        _pluginLoader.LoadModules();\n    }\n\n    private void UpdateModule(string moduleName)\n    {\n        // Update module logic here\n        _pluginLoader.LoadModules();\n    }\n\n    private void DeleteModule(string moduleName)\n    {\n        // Delete module logic here\n        _pluginLoader.LoadModules();\n    }\n}