<asp:TreeView ID="TreeView1" runat="server" OnSelectedNodeChanged="TreeView1_SelectedNodeChanged" DataSourceID="XmlDataSource2" AutoGenerateDataBindings="False">\n  <DataBindings>\n     <asp:TreeNodeBinding DataMember="Node" ValueField="Name" SelectAction="Select" />\n  </DataBindings>\n</asp:TreeView>\n\nprotected void TreeView1_SelectedNodeChanged(object sender, EventArgs e)\n{\n    TreeNode currentNode = TreeView1.SelectedNode;\n    if (currentNode != null)\n    {\n        string url = currentNode["URL"].ToString(); // assuming "URL" is the field in your XML data source\n        iframe1.Src = url; // set the iframe's source URL\n        iframe1.Visible = true; // show the iframe\n    }\n}