public static T[] ZipperMerge<T>(T[] arr1, T[] arr2)\n{\n    T[] result = new T[arr1.Length + arr2.Length];\n    int i = 0, j = 0, k = 0;\n\n    while (i < arr1.Length && j < arr2.Length)\n    {\n        result[k++] = arr1[i++];\n        result[k++] = arr2[j++];\n    }\n\n    while (i < arr1.Length)\n        result[k++] = arr1[i++];\n\n    while (j < arr2.Length)\n        result[k++] = arr2[j++];\n\n    return result;\n}