using Microsoft.SharePoint;\n\n// Assuming you have an SPSite object\nSPSite site = new SPSite("https://your-site-collection-url");\n\n// Get the root web\nSPWeb rootWeb = site.RootWeb;\n\n// Create a new permission level\nSPPermissionLevel permissionLevel = new SPPermissionLevel(rootWeb, "Manage Groups");\n\n// Add the Manage Groups permission\npermissionLevel.AddPermission(SPBasePermissions.ManageGroups);\n\n// Add the permission level to the site\nrootWeb.AddPermissionLevel(permissionLevel);\n\n// Assign the permission level to your account\nSPUser user = rootWeb.EnsureUser("your-account-username");\nuser.AddPermissionLevel(permissionLevel);