protected void Page_Load(object sender, EventArgs e)\n{\n    if (Page.IsPostBack == false)\n    {\n        if (Request.QueryString.Count != 0)\n        {\n            string filterExpression = string.Empty;\n\n            if (Request.QueryString["NAME"] != null)\n            {\n                filterExpression = string.Format("NAME LIKE '%{0}%'", Request.QueryString["NAME"]);\n            }\n\n            this.SqlDataSourceCOUNTRIES.FilterExpression = filterExpression;\n\n            // Use parameterized query to prevent SQL injection\n            this.SqlDataSourceCOUNTRIES.SelectParameters.Add("NAME", Request.QueryString["NAME"]);\n        }\n    }\n}