private readonly SemaphoreSlim _semaphore = new SemaphoreSlim(1, 1);\n\nprivate async void InsertBasicVaraibles(object param)\n{\n    try\n    {\n        await _semaphore.WaitAsync(); // Wait for the semaphore to be released\n        // Insert into DB\n    }\n    catch (Exception ex)\n    {\n        // Handle\n    }\n    finally\n    {\n        _semaphore.Release(); // Release the semaphore\n    }\n}