using System.Data.SQLite;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new SQLite connection\n        using (SQLiteConnection conn = new SQLiteConnection("Data Source=example.db"))\n        {\n            conn.Open();\n\n            // Create a new SQLite command\n            using (SQLiteCommand cmd = new SQLiteCommand(conn))\n            {\n                // Create a table\n                cmd.CommandText = "CREATE TABLE IF NOT EXISTS users (id INTEGER PRIMARY KEY, name TEXT)";\n                cmd.ExecuteNonQuery();\n\n                // Insert data\n                cmd.CommandText = "INSERT INTO users (name) VALUES ('John Doe')";\n                cmd.ExecuteNonQuery();\n\n                // Query data\n                cmd.CommandText = "SELECT * FROM users";\n                using (SQLiteDataReader reader = cmd.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine($"ID: {reader["id"]}, Name: {reader["name"]}");\n                    }\n                }\n            }\n        }\n    }\n}