using System;\nusing System.Runtime.InteropServices;\n\nclass FiddlerProxy\n{\n    [DllImport("httpapi.dll")]\n    static extern unsafe IntPtr HttpCreateServerSession(\n        HTTPAPI_VERSION version,\n        uint flags,\n        IntPtr pReserved,\n        out IntPtr pServerSession\n    );\n\n    // ...\n\n    public void RegisterProxy()\n    {\n        HTTPAPI_VERSION version = new HTTPAPI_VERSION(1, 0);\n        IntPtr serverSession;\n        IntPtr handle = HttpCreateServerSession(\n            version,\n            0,\n            IntPtr.Zero,\n            out serverSession\n        );\n\n        // Register the proxy URL\n        string proxyUrl = "http://127.0.0.1";\n        // ...\n    }\n}