public class DynamicValidator : BaseValidator\n{\n    protected override bool EvaluateIsValid()\n    {\n        string selectedValue = ddl1.SelectedValue;\n        string textBoxValue = tb1.Text;\n\n        if (selectedValue == "1") // First\n        {\n            return textBoxValue.All(char.IsDigit);\n        }\n        else if (selectedValue == "2") // Second\n        {\n            return textBoxValue.All(char.IsLetter);\n        }\n        return false;\n    }\n}