using System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        int[] numbers = { 0, 1, 2, 2, 2, 3, 4, 4, 5 };\n\n        var uniqueNumbers = numbers.GroupBy(n => n)\n                                   .Where(g => g.Count() == 1)\n                                   .Select(g => g.Key);\n\n        foreach (var number in uniqueNumbers)\n        {\n            System.Console.WriteLine(number);\n        }\n    }\n}